import { Divider, Typography } from "@mui/material";
import Grid from "@mui/material/Grid";
import TextField from "@mui/material/TextField";
import { useState } from "react";

export default function CreateListing(){

const [title, setTitle] = useState('');
const [rate, setRate] = useState();
const [location, setLocation] = useState('');
const [description, setDescription] = useState('');

const handleTitleChange = (e) => {
    setTitle(e.target.value);
}
const handleRateChange = (e) => {
    setRate(e.target.value);
}
const handleLocationChange = (e) => {
    setLocation(e.target.value);
}
const handleDescriptionChange = (e) => {
    setDescription(e.target.value);
}
    return(
        <div>
            <Typography variant="h4" sx={{margin:'auto'}}>Create Your Listing</Typography>
            {/*here go the pics*/}
            <Divider sx={{backgroundColor:'peachpuff'}}/>
            <Grid container>
                <Grid xs={2}>
                    <Typography sx={{mt:2}} variant='h6'>Listing Title</Typography>
                    <TextField sx={{mb:2}} placeholder='Enter Title' onChange={handleTitleChange}/>
                </Grid>
                <Grid xs={2}>
                    <Typography sx={{mt:2}} variant='h6'>Nightly Rate</Typography>
                    <TextField sx={{mb:2}} placeholder='Enter Rate' onChange={handleRateChange}/>
                </Grid>
                <Grid xs={2}>
                    <Typography sx={{mt:2}} variant='h6'>Location</Typography>
                    <TextField sx={{mb:2}} placeholder='Enter Location' onChange={handleLocationChange}/>
                </Grid>
            </Grid>
            <Divider sx={{backgroundColor:'peachpuff'}}/>
            <Grid container>
                <Grid xs={6}>
                    <Typography sx={{justifyContent: 'center', display: 'flex',
                    alignItems: 'center', m:2}} variant='h6'>Description
                    </Typography>
                </Grid>
                <Grid xs={6}>
                    <TextField fullWidth multiline rows={6} sx={{justifyContent: 'center', display: 'flex',
                    alignItems: 'center', mb:2}} placeholder='Describe the property...' onChange={handleDescriptionChange}/>
                </Grid>
            </Grid>
            <Divider sx={{backgroundColor:'peachpuff'}}/>
            <Typography sx={{mt:2}} variant='h6'>Amenities</Typography>
        </div>
    )
}
