import { AppBar, Toolbar } from "@mui/material";
import { Typography } from "@mui/material";
import { Link } from "react-router-dom";
import Button from "@mui/material/Button";
import Grid from "@mui/material/Grid";
import { UserContext } from "../../Contexts/UserContext/UserContext";
import { useContext } from "react";
import dayjs from "dayjs";
import { useState } from "react";

export default function MyAppBar(){

const {user, setUser} = useContext(UserContext);
const [isLoggedIn, setIsLoggedIn] = useState(true);


const displayButton = () => {

  var isLoggedIn = true;

  if(user == null){
    isLoggedIn = false;
  }
  else if(dayjs().isAfter(dayjs(user.logTime).add(6, 'hour'))){
    isLoggedIn = false;
  }

  return(
    <div>
      {isLoggedIn ?
      <Link style={{ textDecoration: 'none' }} to={'/mystuff'}>
        <Button variant="contained" sx={{":hover": {
        bgcolor: "peachpuff"}, justifyContent:'right', backgroundColor:"lightsalmon"}}>My Stuff</Button>    
      </Link>
      : 
      <Link style={{ textDecoration: 'none' }} to={'/user/login'}>
        <Button variant="contained" sx={{":hover": {
        bgcolor: "peachpuff"}, justifyContent:'right', backgroundColor:"lightsalmon"}}>Login
        </Button>    
      </Link>}
    </div>
  )
}
    return(

        //the login link button will be username or mystuff when logged in. Some sort of function
        <div>
            <AppBar position="static">
                <Toolbar sx={{backgroundColor:"sandybrown"}}>
                  <Grid container sx={{justifyContent: 'center', display: 'flex',
                  alignItems: 'center'}}>
                    <Grid item xs={2}/>
                    <Grid item sx={{justifyContent: 'center', display: 'flex',
                      alignItems: 'center'}} xs={5}>
                      <Typography variant="h4" sx={{margin:2}}>SpotFinder</Typography>
                    </Grid>
                    <Grid item xs={2}>
                      <Link  style={{ textDecoration: 'none' }} to={'/listings'}>
                        <Button variant="contained" sx={{":hover": {
                        bgcolor: "peachpuff"},justifyContent:'right', marginRight: 3, backgroundColor:"lightsalmon"}}>View Listings</Button>    
                      </Link>
                    </Grid>
                    {displayButton()}
                  </Grid>
                </Toolbar>
            </AppBar>
        </div>
    )

}
