                      </Typography>
                      <Typography variant="body1" color="text.secondary">
                        {dayjs(val.startDate).format('MM/DD/YYYY')} - {dayjs(val.endDate).format('MM/DD/YYYY')}
                      </Typography>
                          <Rating
                          name="user rating"
                          value={val.review.rating}
                          disabled
                          />
                      <Typography variant="body2">
                        "{val.review.text}"
                      </Typography>
                    </CardContent>
                </Card>
            </Grid>

        )
    })
}
const handleClickBooking = () => {

    if(user == null){
        setRequiresLogin(true);
    }
    else if(dayjs().isAfter(dayjs(user.logTime).add(6, 'hour'))){
        setRequiresLogin(true);
    }
    else{
        setDrawerOpen(true);
    }
    //before changing it, onClick for booking button was {() => setDrawerOpen(true)}
}
