                <Grid item xs={5}>
                    {showFailMessage()} 
                </Grid>
            </Grid>
            {modalOpen &&
            
                <Modal
                  open={modalOpen}
                  onClose={() => setModalOpen(false)}
                >
                    <Box sx={style}>
                        <Typography variant="h6">Listing saved!</Typography>
                        <Link style={{ textDecoration: 'none' }} to={`/listings/${listing.id}`}>
                            <Button>Go to your listing</Button>
                        </Link>
                    </Box>
                </Modal>
            }

            </div>
            }
            
        </div>

    )
