import { Divider, Typography } from "@mui/material";
import Grid from '@mui/material/Unstable_Grid2';
import { useState } from "react";
import Button from "@mui/material/Button";
import { useParams } from "react-router-dom";
import dayjs from "dayjs";
import Drawer from "@mui/material/Drawer";
import { DesktopDatePicker } from '@mui/x-date-pickers/DesktopDatePicker';
import Modal from '@mui/material/Modal';
import Box from '@mui/material/Box';
import { UserContext } from "../../Contexts/UserContext/UserContext";
import { useContext } from "react";
import { useEffect } from "react";
import ImageGallery from "../Subcomponents/ImageGallery/ImageGallery";
import AmenitiesList from "../Subcomponents/AmenitiesList/AmenitiesList";
import FaveIcon from "../Subcomponents/FaveIcon/FaveIcon";
import ReviewCard from "../Subcomponents/ReviewCard/ReviewCard";
import BookingDrawer from "../Subcomponents/BookingDrawer/BookingDrawer";


export default function Listing(){

const api = `https://localhost:44305`;

const style = {
    position: 'absolute',
    top: '50%',
    left: '50%',
    transform: 'translate(-50%, -50%)',
    width: 400,
    bgcolor: 'background.paper',
    borderRadius:3,
    boxShadow: 24,
    p: 4,
  };

const {id} = useParams();
const [listingLoaded, setListingLoaded] = useState(false);
const {user, setUser, isLoggedIn, setIsLoggedIn} = useContext(UserContext);
const [listing, setListing] = useState();
//const [drawerOpen, setDrawerOpen] = useState(false);
//const [checkin, setCheckin] = useState('');
//const [checkout, setCheckout] = useState('');
//const [failBooking, setFailBooking] = useState(false);
//const [failBookingMessage, setFailBookingMessage] = useState('');
const [modalOpen, setModalOpen] = useState(false);
const [requiresLogin, setRequiresLogin] = useState(false);
const [loginErrorMessage, setLoginErrorMessage] = useState('');
const [addToFavorites, setAddtoFavorites] = useState(false);

//whereas some pages rely on user existing to render at all, this page relies on isloggedin to render conditionally.
//That's why the verify login method is different. No re-routing occurs, just setting of isloggedin.

const getUser = (id) => {

    fetch(`${api}/bnb/user/${id}`)
    .then((response) => response.json())
    .then((data) => {
        setUser(data.user);
    })
    .then(() => {
        setIsLoggedIn(true);
    });
}

const verifyLogin = () => {

    if(!user){
        //if user is null, parse the cookie. If there's no cookie, id will be NaN. So, either get user by Id if Id has value, or reroute to login.
        var elements = document.cookie.split('=');
        var id = Number(elements[1]);

        if(!isNaN(id)){
            getUser(id);
        }
        else{
            setIsLoggedIn(false);
        }
    }
    else{
        if(dayjs().isAfter(dayjs(user.logTime).add(6, 'hour'))){
            setIsLoggedIn(false);
        }
        else{ 
            setIsLoggedIn(true);
        }
    } 
}
useEffect(() => {
    verifyLogin();
}, []);

const getListing = () => {

    fetch(`${api}/bnb/listing/${id}`)
    .then((response) => response.json())
    .then((data) => {
        debugger;
        setListing(data.listing);
        console.log(data);
    })
    .then(() => {
        setListingLoaded(true);
    });
}
  useEffect(() => {
    getListing();
}, []);

//const showBookingDrawer = () => {

    //const isDisabled = (date) => {
//
    //    for(let i = 0; i < listing.stays.length; i++){
//
    //        if(dayjs(date).isBetween(dayjs(listing.stays[i].startDate), dayjs(listing.stays[i].endDate), 'day', '[)')){
    //            return true;
    //        }
    //        else{
    //            return false;
    //        }
    //    }
    //}
//
    //return(
    //    <Grid container sx={{justifyContent: 'center', display: 'flex', width:400}}>
    //        <Grid item xs={7}>
    //            <Typography variant="h4" sx={{mb:3, mt:3}}>Book a Stay</Typography>
    //            <Divider sx={{backgroundColor:'peachpuff'}}/>
//
    //            <Typography vairant="h6" sx={{mt:4, mb:1}}>When would you like to stay?</Typography>
//
    //            <Typography sx={{mt:3, mb:1}}>Check-in</Typography>
    //            <DesktopDatePicker
    //                label="Check-in"
    //                inputFormat="MM/DD/YYYY"
    //                value={checkin}
    //                shouldDisableDate={isDisabled}
    //                onChange={handleCheckinChange}
    //                />
    //            <Typography sx={{mt:3, mb:1}}>Check-out</Typography>
    //            <DesktopDatePicker
    //                label="Check-out"
    //                inputFormat="MM/DD/YYYY"
    //                value={checkout}
    //                shouldDisableDate={isDisabled}
    //                onChange={handleCheckoutChange}
    //                />
    //            {showBookingMessage()}
    //            <br/>
    //            <Button variant="contained" sx={{":hover": {
    //            bgcolor: "peachpuff"}, mt:3, mr:2, backgroundColor:"lightsalmon"}} 
    //            onClick={confirmBooking}>Confirm
    //            </Button>
    //            <Button variant="contained" sx={{":hover": {
    //            bgcolor: "gray"}, mt:3, backgroundColor:'lightgray'}} 
    //            onClick={() => setDrawerOpen(false)}>Cancel</Button>
    //        </Grid>  
    //    </Grid>
    //)
//}
//const handleCheckinChange = (newValue) => {
//    setCheckin(newValue);
//}
//const handleCheckoutChange = (newValue) => {
//    setCheckout(newValue);
//}
//const showBookingMessage = () => {

    //return(
        //failBooking &&
        //<Typography variant="caption" color="red">{failBookingMessage}</Typography>
    //)
//}
const confirmBooking = () => {

    if(dayjs(checkin).isAfter(dayjs(checkout))){

        setFailBooking(true);
        setFailBookingMessage("Check-in date must be before check-out date.");
    }
    else if(dayjs(checkin).isBefore(dayjs()) || dayjs(checkin).isBefore(dayjs())){

        setFailBooking(true);
        setFailBookingMessage("Check-in and check-out must be future dates.");
    }
    else if((checkin == "" && checkout != "") || (checkin == null && checkout != null)){
        setFailBooking(true);
        setFailBookingMessage("Please select a check-in date.");
    }
    else if((checkout == "" && checkin != "") || (checkout == null && checkin != null)){
        setFailBooking(true);
        setFailBookingMessage("Please select a check-out date.");
    }
    else{

        const APIRequest = {
            GuestId: user.id,
            HostId: listing.hostId,
            ListingId: Number(id),
            StartDate: checkin,
            EndDate: checkout,
        }

        fetch(`${api}/bnb/addstay`, {
            method: 'POST',
            body: JSON.stringify(APIRequest),
            headers: {
                'Content-Type': 'application/json'
            }
        })
            .then((response) => response.json())
            .then((data) => {
                console.log(data);
                setDrawerOpen(false);
                setModalOpen(true);
            });
    }
}
const Reviews = () => {
    
    return listing.stays.map(function(val, index){

        return(
            <ReviewCard stay={val} key={index}/>
        )
    })
}
const handleClickBooking = () => {

    if(!isLoggedIn){
        setRequiresLogin(true);
        setLoginErrorMessage("You must log in to book a stay.");
    }
    else{
        setDrawerOpen(true);
    }
}
const showLoginError = () => {
    return(
        requiresLogin &&
        <Typography variant="caption" color="red">{loginErrorMessage}</Typography>
    )
}
const handleClickFavorite = () => {

    if(!isLoggedIn){
        setRequiresLogin(true);
        setLoginErrorMessage("You must log in to add to favorites.");
    }
    else{

        var APIRequest = {
            UserId: user.id,
            ListingId: listing.id
        };

        fetch(`${api}/bnb/favorite`, {
            method: 'POST',
            body: JSON.stringify(APIRequest),
            headers: {
                'Content-Type': 'application/json'
            }
        })
            .then((response) => response.json())
            .then((data) => {
                console.log(data);
                
                if(data.success){
                    setAddtoFavorites(true);
                }
            });
    }
}

    return(

        <div>     

            {listingLoaded && 

            <div>

            <ImageGallery listing={listing}/>

            <FaveIcon isLoggedIn={isLoggedIn} user={user} handleClickFavorite={handleClickFavorite}
            id={id} addToFavorites={addToFavorites}/>

            <Divider sx={{backgroundColor:'peachpuff'}}/>

            <Grid container sx={{justifyContent: 'center', display: 'flex', margin:2}}>
                <Grid item xs={12} sx={{justifyContent: 'center', display: 'flex'}}>
                    {showLoginError()}
                </Grid>
                <Grid item xs={3}>
                    <Typography sx={{mt:1}} variant='h6'>{listing.title}</Typography>
                    <Typography variant='subtitle1'>{listing.location}</Typography>
                    <Button variant="contained" sx={{":hover": {
                        bgcolor: "peachpuff"}, mt:2, backgroundColor:"lightsalmon"}}
                        onClick={handleClickBooking}>
                        Book A Stay
                    </Button>    

                    <BookingDrawer 
                    listing={listing}
                    confirmBooking={confirmBooking}/>
                    


                                {/*subcomponent here*/}
                    <Drawer open={drawerOpen} anchor={"left"} onClose={() => setDrawerOpen(false)}>
                        {showBookingDrawer()}
                    </Drawer>

                </Grid>
                <Grid item xs={1}>
                    <Typography sx={{mt:1}} variant='h6'>${listing.rate}/Night</Typography>
                </Grid>
            </Grid>
            <Grid container sx={{justifyContent: 'center', display: 'flex', margin:2}}>
                <Grid item xs={4}>
                    <Typography variant='body1'>"{listing.description}"</Typography>           
                </Grid>
            </Grid>

            <Divider sx={{backgroundColor:'peachpuff'}}/>

            <AmenitiesList listing={listing}/>

            <Divider sx={{backgroundColor:'peachpuff'}}/>


            <Grid container sx={{justifyContent: 'center', display: 'flex', margin:2}}> 
                <Grid item xs={4}>
                    <Typography sx={{mt:2}} variant='h6'>Reviews</Typography>
                </Grid>
                <Grid item xs={12}/>
                <Reviews/>
            </Grid>
            <Modal
              open={modalOpen}
              onClose={() => setModalOpen(false)}
            >
                <Box sx={style}>
                    <Typography variant="h5">Booking confirmed! Enjoy your stay</Typography>
                </Box>
            </Modal>

            </div>}
        </div>
    )
}
