import { DesktopDatePicker } from '@mui/x-date-pickers/DesktopDatePicker';
import { TextField, Typography } from '@mui/material';
import dayjs from 'dayjs';
import { TripsContext } from '../../Contexts/TripsContext';
import { useContext } from 'react';
import React from 'react';
import Divider from '@mui/material/Divider';


export default function CreateTrip(){

    const [startDate, setStartDate] = React.useState(dayjs(''));
    const [endDate, setEndDate] = React.useState(dayjs(''));
    //const { trips, setTrips } = useContext(TripsContext);
    //implement this once you actually create the trip

    const handleStartDateChange = (newValue) => {
      setStartDate(newValue);
    }
    const handleEndDateChange = (newValue) => {
        setEndDate(newValue);
    }

    function displayDate(){

        return(
        <Typography style={{margin:15}} variant="h6">
            Range: {dayjs(startDate).format('MM/DD/YYYY')} to {dayjs(endDate).format('MM/DD/YYYY')}
        </Typography>
        )
    }
    //I want it to only display the date once an end date has been determined.
    return(
        <>
        <div style={{margin:15, marginTop:15}}>
            <Typography variant="caption">Select a Date Range-</Typography>
        </div>
        <div style={{margin:15}}>
            <DesktopDatePicker
            label="Trip Start Date"
            inputFormat="MM/DD/YYYY"
            value={startDate}
            onChange={handleStartDateChange}
            renderInput={(params) => <TextField {...params} />}
            />
        </div>
        <div style={{margin:15}}>
            <DesktopDatePicker
            label="Trip End Date"
            inputFormat="MM/DD/YYYY"
            value={endDate}
            onChange={handleEndDateChange}
            renderInput={(params) => <TextField {...params} />}
            />
        </div> 
        {displayDate()}
        <Divider light />
        </>

    )

}
