import { DesktopDatePicker } from '@mui/x-date-pickers/DesktopDatePicker';
import { TextField, Typography } from '@mui/material';
import dayjs from 'dayjs';
import { TripsContext } from '../../Contexts/TripsContext';
import { useContext } from 'react';
import React from 'react';
import Divider from '@mui/material/Divider';
import { useState } from 'react';
import Button from '@mui/material';


export default function CreateTrip(){

    const [startDate, setStartDate] = React.useState(dayjs(''));
    const [endDate, setEndDate] = React.useState(dayjs(''));
    const [location, setLocation] = useState("")
    const [trip, setTrip] = useState();
    const { trips, setTrips } = useContext(TripsContext);


    const handleStartDateChange = (newValue) => {
      setStartDate(newValue);
    }
    const handleEndDateChange = (newValue) => {
        setEndDate(newValue);
    }
    const handleLocationChange = (e) => {
        setLocation(e.target.value);
    }
    const handleTripChange = () => {

        var newArray = trips.map(function(val, index){
            return(
                val.Id
            )
        });

        var nextId = Math.max(...newArray);

        let trip = {
            id: nextId,
            location: location,
            startDate: startDate,
            endDate: endDate,
            crew: null,
            gear: null,
        }

        setTrips([...trips, trip]);
    }

    function displayDate(){

        return(
        <Typography style={{margin:15, fontWeight:'light', fontSize: 16}} variant="h5">
            Range: {dayjs(startDate).format('MM/DD/YYYY')} to {dayjs(endDate).format('MM/DD/YYYY')}
        </Typography>
        )
    }
    //I want it to only display the date once an end date has been determined.
    return(
        <>
        <div style={{margin:15, marginTop:15}}>
            <Typography variant="caption">Select a Date Range</Typography>
            <br/>
            <DesktopDatePicker
            label="Trip Start Date"
            inputFormat="MM/DD/YYYY"
            value={startDate}
            onChange={handleStartDateChange}
            renderInput={(params) => <TextField {...params} />}
            />
        </div>
        <div style={{margin:15}}>
            <DesktopDatePicker
            label="Trip End Date"
            inputFormat="MM/DD/YYYY"
            value={endDate}
            onChange={handleEndDateChange}
            renderInput={(params) => <TextField {...params} />}
            />
        </div> 
        {displayDate()}
        <Divider light />
        <div style={{margin:15}}>
            <Typography variant="caption">Select a Location</Typography>
            <br/>
            <TextField placeholder='Enter Location' onChange={handleLocationChange}></TextField>
        </div>
        <Divider light />
        <div style={{margin:15}}>
            <Typography variant="h4">Crew</Typography>
        </div>
        <Divider light />
        <div style={{margin:15}}>
            <Typography variant="h4">Gear</Typography>
        </div>
        <Divider light />
        <Button sx={{backgroundColor:"gray"}} onClick={handleTripChange}>Save</Button>
        </>

    )

}
