import { useParams } from "react-router-dom";
import { useContext } from "react";
import { TripsContext } from "../../Contexts/TripsContext";
import { useState } from "react";
import { Typography } from "@mui/material";
import Divider from "@mui/material/Divider";
import dayjs from "dayjs";
import ListItem from "@mui/material/ListItem";
import Button from "@mui/material/Button";
import handleClickRemoveCrew from '../CreateTrip';
import List from "@mui/material/List";

export default function Trip(){

    const {trips} = useContext(TripsContext)
    const {id} = useParams();
    const [trip, setTrip] = useState(trips.find(t => t.id == id));

    const showCrewList = () => {

        return trip.crew.map(function(val, index){

            return(
                
                <ListItem key={index}>
                    {val.name} • {val.phone} • {val.email}
                    <Button type="button" onClick={handleClickRemoveCrew} data-value1={val.id}>x</Button>
                </ListItem>                
            )        
        })
    }

    return (
        <>
            <Typography variant="h4" style={{margin:15}}>{trip.location}</Typography>
            <Divider light />
                <div style={{margin:15, marginTop:15}}>
                <Typography variant="h4" sx={{fontSize:30}}>Dates</Typography>
                <Typography style={{margin:15, fontWeight:'light', fontSize: 16}} variant="h5">
                    {dayjs(trip.startDate).format('MM/DD/YYYY')} to {dayjs(trip.endDate).format('MM/DD/YYYY')}
                </Typography>
            </div>
            <Divider light />
            <div style={{margin:15}}>
                <Typography variant="h4" sx={{fontSize:30}}>Location -</Typography>
                <Typography variant="h4" sx={{fontSize:30}}>{trip.location}</Typography>
            </div>
            <Divider light />
            <div style={{margin:15}}>
                <Typography variant="h4" sx={{fontSize:30}}>Crew</Typography>
                <br/>
                <List sx={{
                        width: '100%',
                        maxWidth: 500,
                        bgcolor: 'background.oldlace',
                        position: 'relative',
                        overflow: 'auto',
                        maxHeight: 300,
                        '& ul': { padding: 0 },
                      }}>
                        {showCrewList()}
                </List>
            </div>
        </>
    )
        
        
}
