import { TripsContext } from "../../Contexts/TripsContext";
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import Typography from '@mui/material/Typography';
import { useContext } from "react";
import { CardActionArea } from '@mui/material';
import { Link } from 'react-router-dom';
import dayjs from "dayjs";
import { useState } from "react";


export default function TripsView(){

    const {trips} = useContext(TripsContext);
    const [showUpcomingHeader, setShowUpcomingHeader] = useState(true);
    const [showPastHeader, setShowPastHeader] = useState(true);

    const upcomingHeader = () => {

        if(showUpcomingHeader){
            return <Typography sx={{margin:4}} variant="h5">Upcoming</Typography>
        }
        else{
            return <div></div>
        }
        setShowUpcomingHeader(false);
    }

    const pastHeader = () => {

        if(showPastHeader){
            return <Typography sx={{margin:4}} variant="h5">Past</Typography>
        }
        else{
            return <div></div>
        }
        setShowPastHeader(false);
    }

    //next you want to set trips in reverse order for display...  .reverse()?

    const mapTrips = () => {

        return trips.map(function(val, index){

            if(dayjs(val.endDate).isBefore(dayjs())){

                return(

                    <>
                    {pastHeader()}
                    <Link style={{ textDecoration: 'none' }} underline='none' to={`/trips/${val.id}`} key={index}>
                        <Card sx={{margin:4, bgcolor: 'moccasin'}}>
                            <CardActionArea>
                                <CardContent>
                                    <Typography variant="h5">
                                        <i>
                                        {val.location} ---- {dayjs(val.startDate).format('MM/DD/YYYY')} to {dayjs(val.endDate).format('MM/DD/YYYY')}
                                        </i>
                                    </Typography>
                                </CardContent>
                            </CardActionArea>
                        </Card>
                    </Link>
                    </>
                )
            }
            else{
                
                return(

                    <>
                    {upcomingHeader()}
                    <Link style={{ textDecoration: 'none' }} underline='none' to={`/trips/${val.id}`} key={index}>
                        <Card sx={{margin:4, bgcolor: 'burlywood'}}>
                            <CardActionArea>
                                <CardContent>
                                    <Typography variant="h5">
                                        <i>
                                        {val.location} ---- {dayjs(val.startDate).format('MM/DD/YYYY')} to {dayjs(val.endDate).format('MM/DD/YYYY')}
                                        </i>
                                    </Typography>
                                </CardContent>
                            </CardActionArea>
                        </Card>
                    </Link>       
                    </>   

                )
            }})};
            

    return(
        <div>
            {mapTrips()}
        </div>
    )
}
