import { TripsContext } from "../../Contexts/TripsContext";
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import Typography from '@mui/material/Typography';
import { useContext } from "react";
import { CardActionArea } from '@mui/material';
import { Link } from 'react-router-dom';
import dayjs from "dayjs";
import { useState } from "react";


export default function TripsView(){

    //to-do: sort by date

    const {trips} = useContext(TripsContext);
    const [noUpcoming, setNoUpcoming] = useState(false);
    const [noPast, setNoPast] = useState(false);

    const showUpcomingTrips = () => {

        return trips.map(function(val, index){

            if(dayjs(val.endDate).isAfter(dayjs())){

                return(

                    <Link style={{ textDecoration: 'none' }} underline='none' to={`/trips/${val.id}`} key={index}>
                        <Card sx={{margin:4, bgcolor: 'burlywood'}}>
                            <CardActionArea>
                                <CardContent>
                                    <Typography variant="h5">
                                        <i>
                                        {val.location} ---- {dayjs(val.startDate).format('MM/DD/YYYY')} to {dayjs(val.endDate).format('MM/DD/YYYY')}
                                        </i>
                                    </Typography>
                                </CardContent>
                            </CardActionArea>
                        </Card>
                    </Link>
                )

            }})};

    const showPastTrips = () => {

        return trips.map(function(val, index){

            if(dayjs(val.endDate).isBefore(dayjs())){

                return(

                    <Link style={{ textDecoration: 'none' }} underline='none' to={`/trips/${val.id}/past`} key={index}>
                        <Card sx={{margin:4, bgcolor: 'moccasin'}}>
                            <CardActionArea>
                                <CardContent>
                                    <Typography variant="h5">
                                        <i>
                                        {val.location} ---- {dayjs(val.startDate).format('MM/DD/YYYY')} to {dayjs(val.endDate).format('MM/DD/YYYY')}
                                        </i>
                                    </Typography>
                                </CardContent>
                            </CardActionArea>
                        </Card>
                    </Link>
                )
            }})};
    const noUpcomingMessage = () => {
        
        if(showUpcomingTrips.length === 0){
            setNoUpcoming(true);
        }

        return (
            <div>
            {
            noUpcoming &&   
                <Typography sx={{margin:4}} variant="h5">No upcoming trips...</Typography>
            }
            </div>
        )    
    }
    const noPastMessage = () => {
        
        if(showPastTrips.length === 0){
            setNoPast(true);
        }

        return (
            <div>
            {
            noPast &&   
                <Typography sx={{margin:4}} variant="h5">No past trips...</Typography>
            }
            </div>
        )  
    }
    return(
        <div>
            <Typography sx={{margin:4}} variant="h5">Upcoming</Typography>
            {showUpcomingTrips()}
            {noUpcomingMessage()}
            <Typography sx={{margin:4}} variant="h5">Past</Typography>
            {showPastTrips()}
            {noPastMessage()}
        </div>
    )
}
